
/**
 * CDS Kubernetes Configuration
 */
interface BaseConfig {
    /** image prefix */
    image: string,
    cloudService: string
}

interface RegistryConfig {
    imageRegistry: string
}

interface DeployConfig {
    releaseName: string,
    namespace: string,
    imagePullSecret: string,
    dbSecret: string,
    domain: string
}

interface Service {
    binding: string;
    name: string;
    label: string;
}

interface ApplicationModel {
    isJava: boolean;
    destinations: Map<string,string>; // Unfortunately today needed at build time
    services: Service[]; // Need to know if destination service is required?
}

interface BuildModel extends BaseConfig, ApplicationModel {
}

/**
 * Data required at image push and deploy time
 */
interface PushModel extends BaseConfig, RegistryConfig {
}

/**
 * Data required at chart add time
 */
interface AddChartModel extends BaseConfig, ApplicationModel {
}

interface Model extends BaseConfig, ApplicationModel {
    registry : RegistryConfig,
    deployment : DeployConfig
}